const Name = 'English';

function GetString(str, tokens) {
    switch (str) {
        // connection-manager
        case 'connection-manager/error-ECONNREFUSED-ETIMEDOUT': return `[connection] ERROR: Unable to connect to game server at ${tokens.address}:${tokens.port} (${tokens.code})! Common reasons for this are:`;
        case 'connection-manager/error-ECONNABORTED-ECONNRESET-EPIPE': return `[connection] ERROR: ${tokens.code} - Connection to game server was closed unexpectedly. Common reasons for this are:`;
        case 'connection-manager/connected': return `[connection] routing ${tokens.remote} to ${tokens.remoteAddress}:${tokens.remotePort}`;
        case 'connection-manager/disconnected': return `[connection] ${tokens.remote} disconnected`;

        // loader-cli
        case 'loader-cli/error-node-too-old-1': return 'ERROR: Your installed version of Node.JS is too old to run TERA Server Proxy!';
        case 'loader-cli/error-node-too-old-2': return 'ERROR: Please redownload and reinstall TERA Server Proxy, or install the latest version of Node.JS from https://nodejs.org/en/download/current/';
        case 'loader-cli/error-runtime-incompatible-default': return `ERROR: ${tokens.message}`;
        case 'loader-cli/error-config-corrupt-1': return 'ERROR: Whoops, looks like you\'ve fucked up your config.json!';
        case 'loader-cli/error-config-corrupt-2': return `ERROR: Try to fix it yourself or ask here: ${tokens.supportUrl}`;
        case 'loader-cli/error-cannot-start-proxy': return '[proxy] Unable to start the network proxy, terminating...';
        case 'loader-cli/terminating': return 'terminating...';

        // proxy
        case 'proxy/ready': return '[proxy] Ready, waiting for game client start!';
        case 'proxy/client-interface-error': return '[proxy] ERROR: Unable to start client interface server.';
        case 'proxy/client-interface-error-EADDRINUSE': return '[proxy] ERROR: Another instance of TERA Server Proxy is already running. Please close it or restart your computer and try again!';
        case 'proxy/client-interface-error-EADDRNOTAVAIL': return '[proxy] ERROR: Address not available. Restart your computer and try again!';
        case 'proxy/client-interface-connection-error': return `[proxy] Unable to establish connection to client: ${tokens.error}`;
        case 'proxy/client-interface-connected': return `[proxy] Client ${tokens.justStarted ? 'connected' : 'reconnected'} (${tokens.publisher.toUpperCase()} v${tokens.majorPatchVersion}.${tokens.minorPatchVersion})`;
        case 'proxy/client-interface-disconnected': return `[proxy] Client disconnected`;
        case 'proxy/redirecting-server': return `[proxy] Redirecting ${tokens.name} (${tokens.publisher.toUpperCase()}-${tokens.serverId}) from ${tokens.listen_ip}:${tokens.listen_port} to ${tokens.ip}:${tokens.port}`;
        case 'proxy/warning-unmapped-protocol-1': return `[proxy] WARNING: Unmapped protocol version ${tokens.protocolVersion} (${tokens.publisher.toUpperCase()} v${tokens.majorPatchVersion}.${tokens.minorPatchVersion}).`;
        case 'proxy/warning-unmapped-protocol-2': return '[proxy] WARNING: This can be caused by either of the following:';
        case 'proxy/warning-unmapped-protocol-3': return '[proxy] WARNING: 1) You are trying to play using a newly released client version that is not yet supported.';
        case 'proxy/warning-unmapped-protocol-4': return '[proxy] WARNING:    If there was a game maintenance within the past few hours, please report this!';
        case 'proxy/warning-unmapped-protocol-5': return '[proxy] WARNING:    Otherwise, your client might have been updated for an upcoming patch too early.';
        case 'proxy/warning-unmapped-protocol-6': return '[proxy] WARNING: 2) You are trying to play using an outdated client version.';
        case 'proxy/warning-unmapped-protocol-7': return '[proxy] WARNING:    Try a client repair or reinstalling the game from scratch to fix this!';
        case 'proxy/warning-unmapped-protocol-8': return `[proxy] WARNING: If you cannot fix this on your own, ask for help here: ${tokens.supportUrl}!`;
        case 'proxy/error-cannot-load-protocol': return `ERROR: Unable to load protocol version ${tokens.protocolVersion} (${tokens.publisher.toUpperCase()} v${tokens.majorPatchVersion}.${tokens.minorPatchVersion})!`;
        case 'proxy/protocol-loaded': return `[proxy] Loaded protocol version ${tokens.protocolVersion} (${tokens.publisher.toUpperCase()} v${tokens.majorPatchVersion}.${tokens.minorPatchVersion}).`;
        case 'proxy/error-installing-gpks': return 'ERROR: Unable to install GPK mods! The full error message is:';

        // mod
        case 'mod/prefix-log': return `[${tokens.name}]`;
        case 'mod/prefix-warn': return `[${tokens.name}] WARNING:`;
        case 'mod/prefix-error': return `[${tokens.name}] ERROR:`;
        case 'mod/settings-load-error-corrupted-1': return 'You closed the program improperly the last time you were using it!';
        case 'mod/settings-load-error-corrupted-2': return `This caused the settings for module "${tokens.name}" to become corrupted!`;
        case 'mod/settings-load-error-corrupted-3': return 'The module will load default settings now, so adjust them according to your needs.';
        case 'mod/settings-load-error-corrupted-4': return 'Please remember to close the program properly: first close the game, then close TERA Server Proxy using the X button!';
        case 'mod/settings-load-error-corrupted-5': return 'Do not shut down your computer while TERA Server Proxy is running!';
        case 'mod/settings-load-error-invalid-format-1': return `Invalid settings format for module "${tokens.name}"!`;
        case 'mod/settings-load-error-invalid-format-2': return 'This means that you broke it when manually editing it.';
        case 'mod/settings-load-error-invalid-format-3': return 'Please fix the settings file manually or delete it so that default settings can be restored.';
        case 'mod/settings-load-error-invalid-format-4': return '------------------------------------------';
        case 'mod/settings-load-error-invalid-format-5': return 'Advanced error details';
        case 'mod/settings-load-error-invalid-format-6': return 'The full path to the file is:';
        case 'mod/settings-load-error-invalid-format-7': return `  ${tokens.settingsFile}`;
        case 'mod/settings-load-error-invalid-format-8': return 'The full error message is:';
        case 'mod/settings-load-error-invalid-format-9': return `  ${tokens.e}`;
        case 'mod/settings-load-error-invalid-format-10': return '------------------------------------------';
        case 'mod/settings-save-error-write': return 'Unable to store settings! The full error message is:';
        case 'mod/settings-save-error-stringify': return 'Unable to serialize settings! The full error message is:';
        case 'mod/settings-migrate-error-load-migrator': return 'Unable to load settings migrator! The full error message is:';
        case 'mod/settings-migrate-error-run-migrator': return 'An error occured while migrating the settings! The full error message is:';
        case 'mod/tera-game-state-not-loaded': return 'This mod might malfunction, because "tera-game-state" could not be loaded.';
        case 'mod/client-install-error-1': return `An error occurred while installing the client components!`;
        case 'mod/client-install-error-2': return `Please contact the module's author: ${tokens.supportUrl}`;
        
        case 'mod/mod-preloaded': return `[mods] Preloaded module ${tokens.name}`;
        case 'mod/mod-preload-error-1': return `[mods] ERROR: Module ${tokens.name} could not be preloaded!`;
        case 'mod/mod-preload-error-2': return `[mods] ERROR: Please contact the module's author: ${tokens.supportUrl}`;
        case 'mod/mod-unloaded': return `[mods] Unloaded module ${tokens.name}`;
        case 'mod/mod-reloaded': return `[mods] Reloaded module ${tokens.name}`;
        
        case 'mod/mod-global-instance-loaded': return `[mods] Started module ${tokens.name}`;
        case 'mod/mod-global-instance-load-error-1': return `[mods] ERROR: Module ${tokens.name} could not be started!`;
        case 'mod/mod-global-instance-load-error-2': return `[mods] ERROR: Please contact the module's author: ${tokens.supportUrl}`;
        case 'mod/mod-global-instance-unloaded': return `[mods] Stopped module ${tokens.name}`;
        case 'mod/mod-global-instance-unload-error-1': return `[mods] ERROR: Module ${tokens.name} could not be stopped!`;
        case 'mod/mod-global-instance-unload-error-2': return `[mods] ERROR: Please contact the module's author: ${tokens.supportUrl}`;
        
        case 'mod/mod-client-instance-loaded': return `[mods] Connected module ${tokens.name} to client`;
        case 'mod/mod-client-instance-load-error-1': return `[mods] ERROR: Module ${tokens.name} could not be connected to client!`;
        case 'mod/mod-client-instance-load-error-2': return `[mods] ERROR: Please contact the module's author: ${tokens.supportUrl}`;
        case 'mod/mod-client-instance-unloaded': return `[mods] Disconnected module ${tokens.name} from client`;
        case 'mod/mod-client-instance-unload-error-1': return `[mods] ERROR: Module ${tokens.name} could not be disconnected from client!`;
        case 'mod/mod-client-instance-unload-error-2': return `[mods] ERROR: Please contact the module's author: ${tokens.supportUrl}`;
        
        case 'mod/mod-network-instance-loaded': return `[mods] Applied module ${tokens.name} to server connection`;
        case 'mod/mod-network-instance-load-error-1': return `[mods] ERROR: Module ${tokens.name} could not be applied to server connection!`;
        case 'mod/mod-network-instance-load-error-2': return `[mods] ERROR: Please contact the module's author: ${tokens.supportUrl}`;
        case 'mod/mod-network-instance-unloaded': return `[mods] Removed module ${tokens.name} from server connection`;
        case 'mod/mod-network-instance-unload-error-1': return `[mods] ERROR: Module ${tokens.name} could not be removed from server connection!`;
        case 'mod/mod-network-instance-unload-error-2': return `[mods] ERROR: Please contact the module's author: ${tokens.supportUrl}`;

        // mod-manager
        case 'mod-manager/load-module-info-error': return `[mods] ERROR: Unable to load module information for "${tokens.name}"! The full error message is:`;
        case 'mod-manager/duplicate-mod-error': return `[mods] ERROR: Duplicate module "${tokens.name}" detected!`;
        case 'mod-manager/missing-mod-dependency-error': return `[mods] ERROR: Module ${tokens.name} requires "${tokens.dependency}" to be installed, but it is not!`;
        case 'mod-manager/mod-conflict-error': return `[mods] ERROR: Module ${tokens.name} cannot be loaded while "${tokens.conflict}" is installed!`;
        case 'mod-manager/cannot-load-mod-not-installed': return `[mods] ERROR: Trying to load module that is not installed: ${tokens.name}`;
        case 'mod-manager/cannot-unload-mod-not-installed': return `[mods] ERROR: Trying to unload module that is not installed: ${tokens.name}`;
        case 'mod-manager/cannot-unload-mod-not-loaded': return `[mods] ERROR: Trying to unload module that is not loaded: ${tokens.name}`;
        case 'mod-manager/cannot-reload-mod-not-installed': return `[mods] ERROR: Trying to reload module that is not installed: ${tokens.name}`;
        case 'mod-manager/cannot-reload-mod-not-supported': return `[mods] ERROR: Trying to reload module that does not support hot reloading: ${tokens.name}`;
        case 'mod-manager/cannot-reload-mod-not-loaded': return `[mods] ERROR: Trying to reload module that is not loaded: ${tokens.name}`;

        // default
        default: throw new Error(`Invalid string "${str}"!`);
    }
};

module.exports = { Name, GetString };
